import 'dart:io';import 'package:flutter/widgets.dart';import 'package:flutter_test/flutter_test.dart';import 'package:testautomation/string.dart';class ScreenTowRobot {  const ScreenTowRobot(this.tester);  final WidgetTester tester;  Future<void> findTitle() async {    await tester.pumpAndSettle();    expect(find.text('${StringApp.textSecreenTitle2}'), findsOneWidget);    sleep(const Duration(seconds: 2));  }  Future<void> scrollThePage({bool scrollUp = false}) async {    final listFinder = find.byKey(const Key('singleChildScrollView'));    if (scrollUp) {      await tester.fling(listFinder, const Offset(0, 500), 10000);      await tester.pumpAndSettle();      expect(find.text(StringApp.textSecreenTitle1), findsOneWidget);    } else {      await tester.fling(listFinder, const Offset(0, -500), 10000);      await tester.pumpAndSettle();      expect(find.text(StringApp.textSecreenTitle1), findsOneWidget);    }  }  Future<void> clickbackButton() async {    final btnFinder = find.byKey( Key(StringApp.buttomBack));    await tester.ensureVisible(btnFinder);    await tester.pump(Duration(seconds: 2));    await tester.tap(btnFinder);    await tester.pumpAndSettle(Duration(seconds: 3));  }  Future<void> clickSecondButton() async {    final btnFinder = find.byKey( Key(StringApp.textSecreenTitle2));    await tester.ensureVisible(btnFinder);    await tester.tap(btnFinder);    await tester.pumpAndSettle();  }}