import 'package:flutter/material.dart';import 'package:testautomation/screen_tow.dart';import 'package:testautomation/string.dart';import 'RoundButtomLoading.dart';class LoginScreen extends StatefulWidget {  const LoginScreen({Key? key}) : super(key: key);  @override  State<LoginScreen> createState() => _LoginScreenState();}class _LoginScreenState extends State<LoginScreen> {  bool isLoggedInState = false;  @override  Widget build(BuildContext context) {    return Scaffold(        appBar: AppBar(),        body: Padding(          padding: const EdgeInsets.all(20),          child: SafeArea(            child: isLoggedInState                ? Center(                    child: Text(StringApp.textHomeLoginSucess),                    key: ValueKey(StringApp.textHomeLoginSucess))                : Column(children: [                    TextField(key: ValueKey("userText")),                    TextField(key: ValueKey("passText"), obscureText: true),                    RoundButtomLoading(                      state: 1,                      title: StringApp.buttomlogin,                      press: () {                        //Navigator.pushNamed(context, 'to_screen');                        setState(() {                          isLoggedInState = true;                        });                        Navigator.push(                            context,                            new MaterialPageRoute(                                builder: (context) => new ScreenTow()));                      },                    ),                  ]),          ),        ));  }}