import 'dart:io';import 'dart:ffi';import 'package:flutter/widgets.dart';import 'package:flutter_driver/flutter_driver.dart';import 'package:testautomation/string.dart';class HomeRobot {  const HomeRobot(this.driver);  final FlutterDriver  driver;  Future<void> findTitle() async {    await driver.waitFor(find.text(StringApp.textSecreenTitle1));    sleep(const Duration(seconds: 2));  }  Future<void> findLginSucess() async {    await driver.waitFor(find.text(StringApp.textHomeLoginSucess));    sleep(const Duration(seconds: 2));  }  Future<void> clickFirstButton() async {    await driver.waitFor(find.byValueKey('${StringApp.buttomlogin}'));  //  final btnFinder = find.byValueKey(Key('${StringApp.buttomlogin}'));    await driver.tap(find.byValueKey('${StringApp.buttomlogin}'));  }  Future<void> clickSecondButton() async {    final btnFinder = find.byValueKey( Key(StringApp.textSecreenTitle2)); //   await tester.ensureVisible(btnFinder);    await driver.tap(btnFinder);   // await tester.pumpAndSettle();  }}