import 'dart:io';import 'package:flutter/widgets.dart';import 'package:flutter_driver/flutter_driver.dart';import 'package:testautomation/string.dart';class ScreenTowRobot {  const ScreenTowRobot(this.driver);  final FlutterDriver  driver;  Future<void> findTitle() async {    driver.waitFor(find.text('${StringApp.textSecreenTitle2}'));    sleep(const Duration(seconds: 2));  }  Future<void> clickbackButton() async {    final btnFinder = find.byValueKey('${StringApp.buttomBack}');    sleep(const Duration(seconds: 2));    await driver.tap(btnFinder);  }  Future<void> clickSecondButton() async {    final btnFinder = find.byValueKey( Key(StringApp.textSecreenTitle2));    await driver.tap(btnFinder);  }}